import React, { useEffect } from "react";
import logo from "../resources/images/logo.png";
import ConnectingButtons from "./ConnectingButtons";
import { connect } from "react-redux";
import { setIsRoomHost } from "../store/actions";
import axios from 'axios';
import jwt from 'jsonwebtoken';


import "./IntroductionPage.css";
import GoogleAuthIcon from "./GoogleAuthIcon";
import LogoutButton from "./LogoutButton";

const IntroductionPage = ({ setIsRoomHostAction }) => {

  const fetchUserById = async (userId) => {
    try {
      const response = await axios.get(`/api/users/${userId}`);
      const user = response.data; // Полученный пользователь из ответа сервера
      // Дальнейшая обработка полученного пользователя
      console.log(user);
    } catch (error) {
      console.error(error);
    }
  };

  useEffect(() => {
    setIsRoomHostAction(false);
    //Проверка на jwt 
    const urlParams = new URLSearchParams(window.location.search);
    const jwtToken = urlParams.get("jwt");
    const decodedToken = jwt.decode(jwtToken);
    console.log(decodedToken);

    if (jwtToken) {
      // Сохранение JWT в Local Storage или состоянии компонента
      localStorage.setItem("jwt", jwtToken);
      window.location = 'http://localhost:3000';
    }
  }, []);

  const renderButton = () => {
    const jwtToken = localStorage.getItem("jwt"); // Получаем значение JWT из Local Storage

    if (jwtToken) {
      return <LogoutButton />;
    } else {
      return <GoogleAuthIcon />;
    }
  };

  return (
    <div className="introduction_page_container">
      <div className="profile_data">
        <h2>GoogleId:</h2>
        <h2>Name:</h2>
      </div>
      {renderButton()}
      <div className="introduction_page_panel">
        <img src={logo} className="introduction_page_image"></img>
        <ConnectingButtons />
      </div>
    </div>
  );
};

const mapActionsToProps = (dispatch) => {
  return {
    setIsRoomHostAction: (isRoomHost) => dispatch(setIsRoomHost(isRoomHost)),
  };
};

export default connect(null, mapActionsToProps)(IntroductionPage);
