import React from "react";
import jwt from 'jsonwebtoken';

const Input = ({ placeholder, value, changeHandler }) => {
  return (
    <input
      value={value}
      onChange={changeHandler}
      className="join_room_input"
      placeholder={placeholder}
    />
  );
};

const JoinRoomInputs = (props) => {
  const { roomIdValue, setRoomIdValue, nameValue, setNameValue, isRoomHost } =
    props;

  const jwtToken = localStorage.getItem("jwt");
  let namePlaceholder = "Enter your Name";
  let initialNameValue = nameValue; // Set initialNameValue to the current nameValue

  if (jwtToken) {
    const decodedToken = jwt.decode(jwtToken);
    if (decodedToken && decodedToken.user.username) {
      namePlaceholder = decodedToken.user.username;
      initialNameValue = ""; // Clear initialNameValue if a JWT exists
    }
  }

  const handleRoomIdValueChange = (event) => {
    setRoomIdValue(event.target.value);
  };

  const handleNameValueChange = (event) => {
    setNameValue(event.target.value);
  };

  return (
    <div className="join_room_inputs_container">
      {!isRoomHost && (
        <Input
          placeholder="Enter meeting ID"
          value={roomIdValue}
          changeHandler={handleRoomIdValueChange}
        />
      )}
      <Input
        placeholder={namePlaceholder}
        value={initialNameValue}
        changeHandler={handleNameValueChange}
      />
    </div>
  );
};

export default JoinRoomInputs;
